mapper.select:
		select rnum, no, title, id, writeDate, fileName
		from(	
			select rownum rnum, no, title, id, writeDate, fileName
			from(	
				select no, title, id, writeDate, fileName
				from image 
				
				
				

				
				order by no desc
				)
			)
			where rnum between #{startRow} and #{endRow}
